const moviesContainer = document.getElementById("movies");

function showMovies(list) {
  moviesContainer.innerHTML = "";
  list.forEach(movie => {
    const card = document.createElement("div");
    card.className = "card bg-base-100 shadow-md";
    card.innerHTML = `
      <figure>
        <img src="${movie.rasm}" alt="${movie.nom}" class="h-48 w-full object-cover"/>
      </figure>
      <div class="card-body p-3">
        <h2 class="card-title text-sm">${movie.nom}</h2>
        <p class="text-xs">${movie.janr} | ${movie.yil} | ${movie.mamlakat}</p>
      </div>
    `;
    moviesContainer.appendChild(card);
  });
}

// Filtrlash
function filterMovies(type, value) {
  const filtered = movies.filter(movie => movie[type] === value);
  showMovies(filtered);
}

// Boshlang'ich chiqarish
showMovies(movies);
